package himawari;

public class Himawari 
{
	private static final String PUBLIC_KEY = "";
	private static final String PRIVATE_KEY = "";
	
	
	
	private Scrambler scrambler;
	private HWClient hwClient;
	
	private Himawari() {
		scrambler = new Scrambler(PUBLIC_KEY, PRIVATE_KEY);
		hwClient = HWClient.initializeAndGetClient();
	}
	
	public Scrambler getScrambler() {
		return scrambler;
	}
	public HWClient getClient() {
		return hwClient;
	}
	
	public static Himawari get() {
		return new Himawari();
	}
	


}
